

const elms = document.getElementsByClassName('carousel__swiper');
const runCarouselSlider = () => {
	Array.prototype.forEach.call(elms, el => {
		const swiper = new Swiper(el, {
            // autoHeight: true,
            loop : true,
            slidesPerView : 1,
            effect : 'fade',
            fadeEffect: {
                crossFade: true
              },
            spaceBetween : 0,
            pagination: {
                el: el.querySelector('.carousel__pagination'),
                clickable: true,
              },
        });
	});
};
runCarouselSlider();

