const adjustFontSizeAndSpacing = () => {
    const spans = document.querySelectorAll('.content-with-decorative-text .two_colors--before, .content-with-decorative-text .highlight, .content-with-decorative-text .two_colors--after');

    if (spans.length === 3) {
        let containerWidth = document.querySelector('.content-with-decorative-text').offsetWidth - 30;
        let contentWidth = Array.from(spans).reduce((total, span) => total + span.offsetWidth, 0);

        // Sprawdź szerokość okna i dostosuj szerokość kontenera, jeśli jest większa niż 2000px
        const windowWidth = window.innerWidth;
        if (windowWidth > 2000) {
            containerWidth = 2000;
        }

        const ratio = containerWidth / contentWidth;

        spans.forEach(span => {
            const style = window.getComputedStyle(span);
            const fontSize = parseFloat(style.getPropertyValue('font-size'));
            const letterSpacing = parseFloat(style.getPropertyValue('letter-spacing'));

            span.style.fontSize = `${fontSize * ratio}px`;
            span.style.letterSpacing = `${letterSpacing * ratio}px`;
            span.style.display = 'inline'; // Make sure the text is visible
        });
    }
}

//dom ready
document.addEventListener("DOMContentLoaded", function (event) {
    adjustFontSizeAndSpacing();
});

//resize
window.addEventListener('resize', function (event) {
    adjustFontSizeAndSpacing();
});
